pragma solidity ^0.8.7;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { ITransactionManager } from "../Interfaces/ITransactionManager.sol";
import { ILiFi } from "../Interfaces/ILiFi.sol";
import { LibAsset } from "../Libraries/LibAsset.sol";
import { LibLiFi } from "../Libraries/LibLiFi.sol";

contract NXTPFacet is ILiFi {
    using SafeERC20 for IERC20;

    ITransactionManager private immutable transactionManager;

    event NXTPBridgeStarted(
        address indexed user,
        address indexed router,
        bytes32 indexed transactionId,
        ITransactionManager.TransactionData txData,
        address caller,
        bytes encryptedCallData,
        bytes encodedBid,
        bytes bidSignature
    );

    constructor(ITransactionManager _txMgrAddr) {
        transactionManager = _txMgrAddr;
    }

    struct NXTPData {
        address receivingChainTxManagerAddress;
        address user;
        address router;
        address sendingAssetId;
        address receivingAssetId;
        address sendingChainFallback; // funds sent here on cancel
        address receivingAddress;
        address callTo;
        uint256 sendingChainId;
        uint256 receivingChainId;
        bytes32 callDataHash; // hashed to prevent free option
        bytes32 transactionId;
        bytes encryptedCallData; // optional calldata
        bytes encodedBid; // bid from router
        bytes bidSignature; // router's sig on bid
        uint256 amount;
        uint256 expiry;
    }

    /**
     * @notice This function starts a cross-chain transaction using the NXTP protocol
     * @param _lifiData data used purely for tracking and analytics
     * @param _nxtpData data needed to complete an NXTP cross-chain transaction
     */
    function startBridgeTokensViaNXTP(LiFiData memory _lifiData, NXTPData calldata _nxtpData) public payable {
        // Ensure sender has enough to complete the bridge transaction
        address fromToken = _nxtpData.sendingAssetId;
        if (_nxtpData.sendingAssetId == address(0)) require(msg.value == _nxtpData.amount, "ERR_INVALID_AMOUNT");
        else {
            uint256 _fromTokenBalance = LibAsset.getOwnBalance(fromToken);
            LibAsset.transferFromERC20(fromToken, msg.sender, address(this), _nxtpData.amount);
            require(LibAsset.getOwnBalance(fromToken) - _fromTokenBalance == _nxtpData.amount, "ERR_INVALID_AMOUNT");
        }

        // Start the bridge process
        _startBridge(_nxtpData);

        emit LiFiTransferStarted(
            _lifiData.transactionId,
            _lifiData.integrator,
            _lifiData.referrer,
            _lifiData.timestamp
        );
    }

    /**
     * @notice This function performs a swap or multiple swaps and then starts a cross-chain transaction
     *         using the NXTP protocol.
     * @param _lifiData data used purely for tracking and analytics
     * @param _swapData array of data needed for swaps
     * @param _nxtpData data needed to complete an NXTP cross-chain transaction
     */
    function swapAndStartBridgeTokensViaNXTP(
        LiFiData memory _lifiData,
        LibLiFi.SwapData[] calldata _swapData,
        NXTPData calldata _nxtpData
    ) public payable {
        address fromToken = _nxtpData.sendingAssetId;
        uint256 _fromTokenBalance = LibAsset.getOwnBalance(fromToken);

        // Swap
        for (uint8 i; i < _swapData.length; i++) {
            LibLiFi.swap(_swapData[i]);
        }

        require(LibAsset.getOwnBalance(fromToken) - _fromTokenBalance == _nxtpData.amount, "ERR_INVALID_AMOUNT");

        _startBridge(_nxtpData);

        emit LiFiTransferStarted(
            _lifiData.transactionId,
            _lifiData.integrator,
            _lifiData.referrer,
            _lifiData.timestamp
        );
    }

    /**
     * @notice This function completes a cross-chain transaction using the NXTP protocol.
     */
    function completeBridgeTokensViaNXTP(
        LiFiData memory _lifiData,
        address assetId,
        address recipient,
        uint256 amount
    ) public payable {
        if (LibAsset.isNativeAsset(assetId)) {
            require(msg.value == amount, "INVALID_ETH_AMOUNT");
        } else {
            require(msg.value == 0, "ETH_WITH_ERC");
            LibAsset.transferFromERC20(assetId, msg.sender, address(this), amount);
        }

        LibAsset.transferAsset(assetId, payable(recipient), amount);

        emit LiFiTransferCompleted(
            _lifiData.transactionId,
            _lifiData.integrator,
            _lifiData.referrer,
            _lifiData.timestamp
        );
    }

    function swapAndCompleteBridgeTokensViaNXTP(
        LiFiData memory _lifiData,
        LibLiFi.SwapData[] calldata _swapData,
        address finalAssetId,
        address recipient
    ) public payable {
        uint256 startingBalance = LibAsset.getOwnBalance(finalAssetId);

        for (uint8 i; i < _swapData.length; i++) {
            LibLiFi.swap(_swapData[i]);
        }

        uint256 postSwapBalance = LibAsset.getOwnBalance(finalAssetId);

        uint256 finalBalance;

        if (postSwapBalance > startingBalance) {
            finalBalance = postSwapBalance - startingBalance;
            LibAsset.transferAsset(finalAssetId, payable(recipient), finalBalance);
        }

        emit LiFiTransferCompleted(
            _lifiData.transactionId,
            _lifiData.integrator,
            _lifiData.referrer,
            _lifiData.timestamp
        );
    }

    function _startBridge(NXTPData calldata _nxtpData) internal {
        IERC20 fromToken = IERC20(_nxtpData.sendingAssetId);

        // Give Connext approval to bridge tokens
        LibAsset.approveERC20(IERC20(fromToken), address(transactionManager), _nxtpData.amount);

        uint256 value = LibAsset.isNativeAsset(_nxtpData.sendingAssetId) ? msg.value : 0;

        // Initiate bridge transaction on sending chain
        ITransactionManager.TransactionData memory result = transactionManager.prepare{ value: value }(
            ITransactionManager.InvariantTransactionData(
                _nxtpData.receivingChainTxManagerAddress,
                _nxtpData.user,
                _nxtpData.router,
                _nxtpData.sendingAssetId,
                _nxtpData.receivingAssetId,
                _nxtpData.sendingChainFallback,
                _nxtpData.receivingAddress,
                _nxtpData.callTo,
                _nxtpData.sendingChainId,
                _nxtpData.receivingChainId,
                _nxtpData.callDataHash,
                _nxtpData.transactionId
            ),
            _nxtpData.amount,
            _nxtpData.expiry,
            _nxtpData.encryptedCallData,
            _nxtpData.encodedBid,
            _nxtpData.bidSignature
        );

        emit NXTPBridgeStarted(
            result.user,
            result.router,
            result.transactionId,
            result,
            msg.sender,
            _nxtpData.encryptedCallData,
            _nxtpData.encodedBid,
            _nxtpData.bidSignature
        );
    }

    /**
     * @notice show the NXTP transaction manager contract address
     */
    function getNXTPTransactionManager() external view returns (address) {
        return address(transactionManager);
    }
}
