import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import { LibAsset } from "./LibAsset.sol";

library LibLiFi {
    struct SwapData {
        address fromToken;
        address toToken;
        address callTo;
        uint256 fromAmount;
        uint256 toAmount;
        bytes callData;
    }

    event AssetSwapped(address fromToken, address toToken, uint256 fromAmount, uint256 toAmount);

    function swap(SwapData calldata _swapData) internal {
        address fromToken = _swapData.fromToken;
        if (!LibAsset.isNativeAsset(fromToken) && LibAsset.getOwnBalance(fromToken) != _swapData.fromAmount) {
            LibAsset.transferFromERC20(_swapData.fromToken, msg.sender, address(this), _swapData.fromAmount);
        }

        LibAsset.approveERC20(IERC20(fromToken), _swapData.callTo, _swapData.fromAmount);
        (bool success, ) = _swapData.callTo.call{ value: msg.value }(_swapData.callData);
        require(success, "ERR_DEX_SWAP_FAILED");

        emit AssetSwapped(_swapData.fromToken, _swapData.toToken, _swapData.fromAmount, _swapData.toAmount);
    }
}
